using System;
using System.Collections;
using System.Collections.Generic;
using System.Drawing;
using System.IO;
using System.Linq;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Logging;
using WookieBooks.Models;

// For more information on enabling Web API for empty projects, visit https://go.microsoft.com/fwlink/?LinkID=397860

namespace WookieBooks.Controllers
{
    [Route("books")]
    [ApiController]
    public class BooksController : ControllerBase
    {
        static Dictionary<int, Book> _books;
        static BooksController()
        {
            _books = new Dictionary<int, Book>();

            _books.Add(1, new Book
            {
                Id = 1,
                Author = "Barack Obama",
                Description = "A Promised Land is a memoir by Barack Obama, the 44th President of the United States from 2009 to 2017",
                ImageData = "",
                Price = 34.99f,
                Title = "A Promised Land"
            });
            _books.Add(2, new Book
            {
                Id = 2,
                Author = "Tony Faggioli",
                Description = "Detective Napoleon Villa is on a mission to save Kyle Fasano, the man who was once his primary suspect and who may now instead become the next victim in a case that continues to spiral out of control. Drawing on all his skills isn't enough though, as nothing could've prepared Napoleon for what's about to happen next",
                ImageData = "",
                Price = 24.99f,
                Title = "A Million to One"
            });
        }
       

        // GET: /<BooksController>
        [HttpGet]
        public List<Book> GetAllBooks()
        {
            lock (((IDictionary)_books).SyncRoot)
            {
                return _books.Values.ToList();
            }
        }


        // GET /<BooksController>/5
        [HttpGet("{id}")]
        public Book GetBookById(int id)
        {
            //Invalid ID Check
            if (id < 1)
            {
                throw new Exception("Invalid ID received");
            }
            lock (((IDictionary)_books).SyncRoot)
            {
                //Book Not in collection
                if (!_books.ContainsKey(id))
                {
                    throw new Exception("Requested ID not found in book store.");
                }
                else
                    return _books[id];
            }
        }


        // GET /<BooksController>/authors/Barack Obama
        [HttpGet("authors/{author}")]
        public List<Book> GetBooksByAuthor(string author)
        {
            //Invalid ID Check
            if (string.IsNullOrEmpty(author))
            {
                throw new Exception("Invalid Author name received");
            }
            lock (((IDictionary)_books).SyncRoot)
            {
                var booksByAuthor = _books.Values.Where(x => x.Author.Equals(author, StringComparison.OrdinalIgnoreCase));
                //Book Not in collection
                if (booksByAuthor.Count() < 1)
                {
                    throw new Exception("Requested author not found in book store.");
                }
                else
                    return booksByAuthor.ToList();
            }
        }

        // POST /<BooksController>
        [HttpPost]
        public ReturnStatus AddBook([FromBody] Book bookData)
        {
            lock (((IDictionary)_books).SyncRoot)
            {
                if (bookData != null && bookData.Id == 0)
                {
                    if (_books.Count > 0)
                        bookData.Id = _books.Keys.Max() + 1;
                    else
                        bookData.Id = 1;
                }
                var isValidBookData = ValidateBookData(bookData);
                if (!isValidBookData.IsSuccess)
                    return isValidBookData;

                //Book Not in collection
                if (_books.ContainsKey(bookData.Id))
                {
                    return (new ReturnStatus
                    {
                        IsSuccess = false,
                        ReturnMessage = "Requested ID already exists in book store."
                    });
                }
                _books.Add(bookData.Id, bookData);
                return (new ReturnStatus
                {
                    IsSuccess = true,
                    ReturnMessage = $"{bookData.Title} has been added to the book store."
                });
            }
        }



        // PUT /<BooksController>/5
        [HttpPut("{id}")]
        public ReturnStatus UpdateBook(int id,[FromBody] Book bookData)
        {
            if(bookData!=null && bookData.Id!=id)
            {
                return (new ReturnStatus
                {
                    IsSuccess = false,
                    ReturnMessage = "Requested ID in URL does not match with ID in request body."
                });
            }
            var isValidBookData = ValidateBookData(bookData);
            if (!isValidBookData.IsSuccess)
                return isValidBookData;
            lock (((IDictionary)_books).SyncRoot)
            {
                //iD should exist
                if (!_books.ContainsKey(bookData.Id))
                {
                    return (new ReturnStatus
                    {
                        IsSuccess = false,
                        ReturnMessage = "Requested ID not found in book store."
                    });
                }
                else
                {
                    //Update the book object
                    _books[bookData.Id] = bookData;
                    return (new ReturnStatus
                    {
                        IsSuccess = true,
                        ReturnMessage = $"{bookData.Title} details updated in book store."
                    });
                }
            }
        }

        // DELETE /<BooksController>/5
        [HttpDelete("{id}")]
        public ReturnStatus DeleteBookById(int id)
        {
            //Invalid ID Check
            if (id < 1)
            {
                return (new ReturnStatus
                {
                    IsSuccess = false,
                    ReturnMessage = "Invalid ID received."
                });
            }
            lock (((IDictionary)_books).SyncRoot)
            {
                //Book Not in collection
                if (!_books.ContainsKey(id))
                {
                    return (new ReturnStatus
                    {
                        IsSuccess = false,
                        ReturnMessage = "Requested ID not found in book store."
                    });
                }
                else
                {
                    string title = _books[id].Title;
                    _books.Remove(id);
                    return (new ReturnStatus
                    {
                        IsSuccess = true,
                        ReturnMessage = $"{title} has been removed from book store"
                    });
                }
            }
        }

        // DELETE /<BooksController>/authors/Barack Obama
        [HttpDelete("authors/{author}")]
        public ReturnStatus DeleteBookByAuthor(string author)
        {
            //Invalid ID Check
            if (string.IsNullOrEmpty(author))
            {
                return (new ReturnStatus
                {
                    IsSuccess = false,
                    ReturnMessage = "Invalid Author name received"
                });
            }
            lock (((IDictionary)_books).SyncRoot)
            {
                var booksByAuthor = _books.Values.Where(x => x.Author.Equals(author, StringComparison.OrdinalIgnoreCase));
                //Book Not in collection
                if (booksByAuthor.Count() < 1)
                {
                    return (new ReturnStatus
                    {
                        IsSuccess = false,
                        ReturnMessage = "Requested author not found in book store."
                    });
                }
                else
                {
                    int deletedBookCount = booksByAuthor.Count();
                    foreach (var book in booksByAuthor)
                    {
                        _books.Remove(book.Id);
                    }
                    return (new ReturnStatus
                    {
                        IsSuccess = true,
                        ReturnMessage = $"Deleted {deletedBookCount} books by author {author} from book store."
                    });
                }
            }
        }

        private ReturnStatus ValidateBookData(Book bookData)
        {
            ReturnStatus returnStatus = new ReturnStatus { IsSuccess = true };
            if (bookData == null)
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage = "Book data not provided in input";
                return returnStatus;
            }
            if (bookData.Id < 1)
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage = "Invalid ID received";
            }
            if (string.IsNullOrEmpty(bookData.Title))
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage += (string.IsNullOrEmpty(returnStatus.ReturnMessage) ? string.Empty : Environment.NewLine) + "Title cannot be blank";
            }
            if (string.IsNullOrEmpty(bookData.Author))
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage += (string.IsNullOrEmpty(returnStatus.ReturnMessage) ? string.Empty : Environment.NewLine) + "Author cannot be blank";
            }
            if (string.IsNullOrEmpty(bookData.ImageData))
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage += (string.IsNullOrEmpty(returnStatus.ReturnMessage) ? string.Empty : Environment.NewLine) + "Image Data cannot be blank";
            }
            else if (!IsValidImage(bookData.ImageData))
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage += (string.IsNullOrEmpty(returnStatus.ReturnMessage) ? string.Empty : Environment.NewLine) + "Image Data provided is invalid";
            }
            if (bookData.Price < 0)
            {
                returnStatus.IsSuccess = false;
                returnStatus.ReturnMessage += (string.IsNullOrEmpty(returnStatus.ReturnMessage) ? string.Empty : Environment.NewLine) + "Invalid price value received";
            }
            return returnStatus;
        }


        private bool IsValidImage(string imageData)
        {
            try
            {
                byte[] bytes = Convert.FromBase64String(imageData);
                Image image;
                using (MemoryStream ms = new MemoryStream(bytes))
                {
                    image = Image.FromStream(ms);
                }
                return true;
            }
            catch
            {
                return false;
            }
        }


    }
}
